var ap = function(k) {
  return function(f, x) { 
    return function() {
      return k[f].apply(null, [x].concat([].slice.call(arguments)));
    };
  };
};

var infix = function k(x) {
  var f, q = {}, apk = ap(k);
  for (f in k) q[f] = apk(f, x);
  return q;
};

var add = function(a, b) {
  return a + b;
};

var $ = infix;
$.add = add;
console.log($("Well, ").add("hello"));
console.log($(1).add(2));
